    public void guardar() {

        int IDuser, IDCaracteristic;
        Calendar cal = Calendar.getInstance();
        String fecha, hora, Nombre, Diseas1, Diseas2, Diseas3;

        try {
            IDuser = Integer.parseInt(cedi.getText().trim());
            IDCaracteristic = Integer.parseInt(IdCaracteristica.getText().trim());
            Nombre = Name.getText().trim();
            fecha = cal.get(Calendar.DATE) + "/" + cal.get(Calendar.MONTH) + "/" + cal.get(Calendar.YEAR);
            hora = cal.get(Calendar.HOUR_OF_DAY) + ":" + cal.get(Calendar.MINUTE) + ":" + cal.get(Calendar.SECOND);
            Diseas1 = Sintomas1.getSelectedItem().toString();
            Diseas2 = Sintomas2.getSelectedItem().toString();
            Diseas3 = Sintomas3.getSelectedItem().toString();

            String sql = "INSERT INTO Historia(idCaracteristica,Cedula,Nombre,Sintoma1,Sintoma2,Sintoma3,fecha,Hora) VAlUES (" + IDCaracteristic + "," + IDuser + ",'" + Nombre + "','" + Diseas1 + "','" + Diseas2 + "','" + Diseas3 + "','" + fecha + "','" + hora + "')";
            System.out.println("sql= " + sql);
            int x = this.stmt.executeUpdate(sql);
            JOptionPane.showMessageDialog(this, "Mensaje: modificados " + x);
        } catch (NumberFormatException e) {
            System.out.println(e.getMessage());
        } catch (SQLException ex) {
            Logger.getLogger(MainMenu.class.getName()).log(Level.SEVERE, null, ex);
        }
    }

    public void VISUAL(String cedula) {

        String nom = "", ape = "";
        String sql = "SELECT * FROM General_User_Adm WHERE cedula='" + cedula + "'";
        Statement st;
        try {
            st = con.createStatement();
            ResultSet rs = st.executeQuery(sql);
            while (rs.next()) {
                nom = rs.getString("Nombre");
                ape = rs.getString("Apellido");
                Name.setText(nom + " " + ape);
                cedi.setText(cedula);
            }
        } catch (SQLException ex) {
            Logger.getLogger(MainMenu.class.getName()).log(Level.SEVERE, null, ex);
        }
    }

